mapper.select:
SELECT students_info.id as student_id, students_info.year as student_year, students_info.faculty as student_faculty, students_info.speciality as student_speciality, users_info.id as user_id, users_info.name as user_name, users_info.surname as user_surname, users_info.email as user_email, users_info.password as user_password, users_info.role as user_role, groups.id as group_id, groups.number as group_number FROM students_info LEFT JOIN users_info ON (students_info.user_id = users_info.id) LEFT JOIN groups ON (students_info.group_id = groups.id)
WHERE students_info.faculty = #{studentSearchCriteria.faculty}
WHERE students_info.speciality = #{studentSearchCriteria.speciality}
WHERE students_info.year = #{studentSearchCriteria.year}
